export default function HolySites() {
  const [selectedSite, setSelectedSite] = useState("");

  return (
    <div className="holy-site-container">
      <label htmlFor="holy-sites"  className="holy-site-header">Islamic Holy Sites:</label>
      <select id="holy-sites" onChange={(e) => setSelectedSite(e.target.value)}>
        <option value="">Select a site</option>
        {Object.keys(sitesInfo).map(site => (
          <option key={site} value={site}>{site.charAt(0).toUpperCase() + site.slice(1)}</option>
        ))}
      </select>

       <div className="holy-site-buttons">
                {Object.keys(sitesInfo).map((site) => (
                    <button key={site} onClick={() => setSelectedSite(site)}>
                        {site.charAt(0).toUpperCase() + site.slice(1)}
                    </button>
                ))}
            </div>

      {selectedSite && (
        <div>
             <strong className="holy-site-description">{sitesInfo[selectedSite].description}</strong>
             <div className="holy-site-images">
             <HolysiteCoursoul siteImage={sitesInfo[selectedSite].images}/>
            </div>
         
        </div>
      )}
    </div>
  );
}
